import axios from 'axios'
export const getAllFood = () => async dispatch => {
    dispatch({ type: 'GET_FOOD_REQUEST' })
    try {
        const response = await axios.get('/api/food/getAllFood')
        console.log(response);
        dispatch({
            type: 'GET_FOOD_SUCCESS',
            payload: response.data
        })
    }
    catch (error) {
        dispatch({
            type: 'GET_FOOD_FAILED',
            payload: error
        })
    }
}

export const filterFood = (searchKey, category) => async dispatch => {
    var filteredFood;
    dispatch({ type: 'GET_FOOD_REQUEST' })
    try {
        const response = await axios.get('/api/food/getAllFood')
        console.log(response);
        filteredFood = response.data.filter(food => food.name.toLowerCase().includes(searchKey))
        dispatch({
            type: 'GET_FOOD_SUCCESS',
            payload: response.filteredFood
        })
    }
    catch (error) {
        dispatch({
            type: 'GET_FOOD_FAILED',
            payload: error
        })
    }
}
