import React, { useState } from 'react'

const Pizza = ({ pizza }) => {
    const [quantity, setQuantity] = useState(1);
    const [variant, setVariant] = useState('small');
    return (
        <div>
            <h1>{pizza.name}</h1>
            <img src={pizza.image} className="img-fluid" style={{ height: '200px', width: '200px' }} alt="pizza" />
            <div className="flex-container">
                <div className="w-100 m-1">
                    <p>Varients</p>
                    <select className='form-control' value={variant} onChange={(e) => { setVariant(e.target.value) }}>{pizza.varients.map(varient => {
                        return <option value={varient}>{varient}</option>
                    })}</select>
                </div>
                <div className="w-100 m-1">
                    <p>Quantity</p>
                    <select className='form-control' value={quantity} onChange={(e) => { setQuantity(e.target.value) }}>
                        {[...Array(10).keys()].map((x, i) => {
                            return <option value={i + 1}>{i + 1}</option>
                        })}
                    </select>
                </div>
            </div>
            <div className="flex-container">
                <div className="m-1 w-100">
                    <h1>Prices: {pizza.prices[0][variant] * [quantity]}</h1>
                </div>
                <div className='m-1 w-100'>
                    <button className="btn">ADD TO CART</button>
                </div>
            </div>
        </div>
    )
}

export default Pizza
